package myfirstJavaProgram;

/**
 * @author Ajmi
 *
 */
public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("Hello World\n");
		System.out.println("\tHello New World");
		System.out.println();
		//sysout + Cntrl + Space => System.out.println();
		
		int x; //declaration
		x = 123; // assignment
		int y = 345; //initialization
		System.out.println("My int is "+x +y);
		
		long z = 3453534523154585467L; //initialization
		System.out.println("My long is "+z);
		
		float w = 3.45f; //initialization
		System.out.println("My float is "+w);
		
		double u  = 3412.34545; //initialization
		System.out.println("My double is "+u);
		
		boolean v = true; //initialization
		System.out.println("My boolean is "+v);
		
		char t = '@'; //initialization
		System.out.println("My charcater is "+t);
		
		String name = "Ajmi"; //initialization
		System.out.println("My name is "+name);
	}

}